import React, { Component } from "react";

class McGrathNicol extends Component {	 
	render() {
		return (
            <div className='subheader'>
                <div className='major'>
                    <br></br>
                    <p>McGrathNicol is a specialist advisory and restructuring firm with a vision to manage complexity and consistently deliver results depending on client need. The team operates across all industry sectors and business sizes to deliver advice to ensure high-quality outcomes. </p>

                    <p>On the Advisory branch, McGrathNicol works with companies to improve their performance by mitigating risk, managing change and achieving growth. Their expertise lies in Forensic, Governance, Risk and Compliance, Strategy and Business Improvement, Technology and Transactions.</p>

                    <p>On the Restructuring branch, McGrathNicol aims to restore stability and deliver value to businesses experience operational or financial difficulties, with a focus in Special Situations, Turnaround and Insolvency.</p>

                    <p>McGrathNicol is committed to fostering a diverse workplace which is reflected in their involvement with Women on Boards which aims to support women in leveraging their professional skills and experience in leadership roles.</p>

                </div>
                <br></br>
                <a href="https://www.mcgrathnicol.com/" className="button" >Visit McGrathNicol</a>
            </div>
        );
	}
}

export default McGrathNicol;
